INSERT INTO `mantis_bug_file_table` VALUES (1,24,'','','0000024-sitetrait.xml','0000024-sitetrait.xml','',362,'text/xml','2004-07-14 15:49:09','<?xml version=\"1.0\"?>\r\n<NAXML-SiteTraits>\r\n   <SiteTrait>\r\n      <SiteID>0001</SiteID>\r\n      <TraitName>pos</TraitName>\r\n      <TraitValue>gsite</TraitValue>\r\n      <TraitRank>0</TraitRank>\r\n   </SiteTrait>\r\n   <SiteTrait>\r\n      <SiteID>0001</SiteID>\r\n      <TraitName>Deli</TraitName>\r\n      <TraitValue>no</TraitValue>\r\n   </SiteTrait>\r\n</NAXML-SiteTraits>\r\n'),(2,24,'','','0000024-cidsstrt.xsd','0000024-cidsstrt.xsd','',694,'text/xml','2004-07-14 15:49:48','<xsd:schema xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\"\r\n            xmlns:sql=\"urn:schemas-microsoft-com:mapping-schema\">\r\n  <xsd:element name=\"SiteTrait\" sql:relation=\"cidsstrt\" >\r\n    <xsd:complexType>\r\n     <xsd:sequence>\r\n       <xsd:element name=\"SiteID\"  sql:field=\"[cidsstrt_location_id]\"  type=\"xsd:integer\"/>\r\n       <xsd:element name=\"TraitName\" sql:field=\"[cidsstrt_trait_name]\" type=\"xsd:string\"/>\r\n       <xsd:element name=\"TraitValue\"  sql:field=\"[cidsstrt_trait_value]\" type=\"xsd:string\"/>\r\n       <xsd:element name=\"TraitRank\"  sql:field=\"[cidsstrt_trait_rank]\" default=\"0\"  type=\"xsd:integer\"/>\r\n     </xsd:sequence>\r\n    </xsd:complexType>\r\n  </xsd:element>\r\n</xsd:schema>\r\n'),(3,43,'','','./Uploads/POS33\\0000043-NAXML-PBIMovement33_modified.xsd','0000043-NAXML-PBIMovement33_modified.xsd','./Uploads/POS33\\',28500,'text/xml','2005-01-21 13:57:23','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<!-- edited with XML Spy v4.3 U (http://www.xmlspy.com) by Fred Hoff (AutoGas) -->\r\n<!-- edited with XMLSPY v2004 rel. 4 U (http://www.xmlspy.com) by John C Hervey (NACS) -->\r\n<!--Copyright Petroleum Convenience Alliance for Technology Standards 2004. All Rights Reserved.\r\nThis document may be copied, used or shared among PCATS members for purposes consistent with adoption of the PCATS Standards; however, any inconsistent uses must be pre-approved in writing by the Petroleum Convenience Alliance for Technology Standards.  As such, this document may not be furnished to non-members of PCATS, and derivative works that comment on or otherwise explain it or assist in its implementation may not cite or refer to the standard, specification, protocol or guideline, in whole or in part, without such permission.  Moreover, this document may not be modified in any way, including removal of the copyright notice or references to PCATS.  Translations of this document into languages other than English shall continue to reflect the PCATS copyright notice.\r\nThe limited permissions granted above are perpetual and will not be revoked by the Petroleum Convenience Alliance for Technology Standards or its successors or assigns. -->\r\n<!-- \r\n	History\r\n	David Ezell	2002-07		Creation\r\n	David Ezell	2002-11-07	      Fixes based on UML importation attempt, per John Hervey\r\n	David Ezell 2003-10-24      Changes based on diffs between version 3.2 and version3.3 of the DTDs\r\n	David Ezell 2003-12-14      Submitted changes for POSBO approval\r\n	David Ezell 2004-01-09      Changed ComboID instances to PromotionID\r\n	David Ezell 2004-02-17      Removed NACS Copyright and replaced with PCATS Copyright\r\n	David Ezell 2004-03-03      Made Tender element optional in MSMSalesTotals\r\n							   		Restrict TenderTransactionsCount to only occur if Tender occurs in MSMSalesTotals\r\n`									Changed annotation for FuelProductMovement\r\n									CBMSalesTotals/MerchandiseCode and CBMSalesTotals/Tender both optional\r\n									TLMDetail/MerchandiseCode optional\r\n	David Ezell 2004-03-12		Added PriceOverrideAmount and PriceOverrideCount to ISMSalesTotals\r\n	John Hervey 2004-03-15 Changed TankProductMovement to include TankProdMovementHeader v. SalesMovementHeader\r\n	John Hervey 2004-03-15 Changed OpenDepartmentSalesCount to OpenDepartmentTransactionCount to be consistent with dtd and other counts\r\n-->\r\n<xs:schema targetNamespace=\"http://www.naxml.org/POSBO/Vocabulary/2003-10-16\" xmlns:xs=\"http://www.w3.org/2001/XMLSchema\" xmlns:nax=\"http://www.naxml.org/POSBO/Vocabulary/2003-10-16\" elementFormDefault=\"qualified\" attributeFormDefault=\"unqualified\">\r\n	<xs:include schemaLocation=\"NAXML-PBIDictionary33.xsd\"/>\r\n	<xs:include schemaLocation=\"NAXML-PBIUtility33.xsd\"/>\r\n	<!--NAXML-MovementReport structure-->\r\n	<xs:element name=\"NAXML-MovementReport\">\r\n		<xs:annotation>\r\n			<xs:documentation>MovementReport contains a TransmissionHeader and some set of\r\nxxxMovement reports. NAXML-PBIDictionary.dtd contains all of the elements listed in\r\nthe NACS Point-of-Sale Back Office Interface Guidelines Version 3.3</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:TransmissionHeader\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:BatchSummaryMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:ComboSalesMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:FuelGradeMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:FuelProductMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:ItemSalesMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:MerchandiseCodeMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:MiscellaneousSummaryMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:TankProductMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:TaxLevelMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:TenderSummaryMovement\" minOccurs=\"0\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n			<xs:attribute name=\"version\" fixed=\"3.3\"/>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"MovementHeader\">\r\n		<xs:annotation>\r\n			<xs:documentation>Each xxxMovement element contains a MovementHeader and either a\r\nSalesMovementHeader or a TankProdMovementHeader.  MovementHeader contains a set of elements that identify the specific\r\nreport as to date/time and reporting period. Optional \"qualifier\" fields\r\nare also included for a finer grained report.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ReportSequenceNumber\"/>\r\n				<xs:element ref=\"nax:PrimaryReportPeriod\"/>\r\n				<xs:element ref=\"nax:SecondaryReportPeriod\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:BusinessDate\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:BeginDate\"/>\r\n				<xs:element ref=\"nax:BeginTime\"/>\r\n				<xs:element ref=\"nax:EndDate\"/>\r\n				<xs:element ref=\"nax:EndTime\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"SalesMovementHeader\">\r\n		<xs:annotation>\r\n			<xs:documentation>SalesMovementHeader contains a set of elements that identify\r\nthe specific report as to cashier, register and till </xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:RegisterID\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:CashierID\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TillID\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:EmployeeNumber\" minOccurs=\"0\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"TankProdMovementHeader\">\r\n		<xs:annotation>\r\n			<xs:documentation>TankProdMovementHeader contains a set of elements that identify the\r\nspecific report as to date/time of the tank inventory reading</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ReadingDate\"/>\r\n				<xs:element ref=\"nax:ReadingTime\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--ComboSalesMovement structures-->\r\n	<xs:element name=\"ComboSalesMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Combo Sales Movement(CBM) – Contains those data elements the POS system needs to send to the BO system to \"combo\" sales.  A combo is defined as\r\n“selling a specific set of items at a promotional price such as 1\r\ndrink, 1 sandwich, and 1 bag of chips.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:CBMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"CBMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:PromotionID\"/>\r\n				<xs:element ref=\"nax:ComboDescription\"/>\r\n				<xs:element ref=\"nax:ComboPrice\"/>\r\n				<xs:element ref=\"nax:ComboList\" maxOccurs=\"unbounded\"/>\r\n				<xs:element ref=\"nax:CBMSalesTotals\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"CBMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MerchandiseCode\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:Tender\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:ActualSalesPrice\"/>\r\n				<xs:element ref=\"nax:CBMReason\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:SellingUnits\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:Currency\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:InventoryValuePrice\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PromotionAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PromotionCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RegularSellPrice\"/>\r\n				<xs:element ref=\"nax:SalesQuantity\"/>\r\n				<xs:element ref=\"nax:SalesAmount\"/>\r\n				<xs:element ref=\"nax:TransactionCount\" minOccurs=\"0\"/>\r\n				<!-- changed nax:Currency to nax:CurrencyCode -->\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"CBMReason\" type=\"nax:cbmReason\"/>\r\n	<!--FuelGradeMovement structures-->\r\n	<xs:element name=\"FuelGradeMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Fuel Grade Movement (FGM) - Contains those data elements the\r\nPOS system needs to send to the BO system to report fuel grade\r\nlevel sales. For a complete definition of fuel grade refer to the\r\nGlossary. It is implied that all fuel grade sales are included in the\r\nMerchandise Code Movement table (MCM) at the summary level\r\neither in total or by grade depending upon implementation setup.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:FGMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelGradeID\"/>\r\n				<xs:element ref=\"nax:FGMNonResettableTotal\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMTenderSummary\"/>\r\n					<xs:element ref=\"nax:FGMSellPriceSummary\"/>\r\n					<xs:element ref=\"nax:FGMReasonSummary\"/>\r\n					<xs:element ref=\"nax:FGMPositionSummary\"/>\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n					<xs:group ref=\"nax:extension\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMTenderSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:Tender\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMSellPriceSummary\"/>\r\n					<xs:element ref=\"nax:FGMReasonSummary\"/>\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMSellPriceSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ActualSalesPrice\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMReasonSummary\"/>\r\n					<xs:element ref=\"nax:FGMPositionSummary\"/>\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMReasonSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FGMReason\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMPositionSummary\"/>\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMPositionSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelPositionID\"/>\r\n				<xs:element ref=\"nax:FGMNonResettableTotal\" minOccurs=\"0\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMReason\" type=\"nax:fgmReason\"/>\r\n	<xs:element name=\"FGMTimeTierSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:TimeTierCode\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMPriceTierSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:PriceTierCode\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMServiceLevelSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ServiceLevelCode\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMOutsideSalesSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:OutsideSalesFlag\"/>\r\n				<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelGradeSalesVolume\"/>\r\n				<xs:element ref=\"nax:FuelGradeSalesAmount\"/>\r\n				<xs:element ref=\"nax:PumpTestTotals\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TaxExemptSalesVolume\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DispenserDiscountAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DispenserDiscountCount\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"PumpTestTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:PumpTestAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PumpTestVolume\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:ReturnTankID\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMNonResettableTotal\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelGradeNonResettableTotalVolume\"/>\r\n				<xs:element ref=\"nax:FuelGradeNonResettableTotalAmount\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--FuelProductMovement structures-->\r\n	<xs:element name=\"FuelProductMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Fuel Product Movement (FPM) Allows the transmission of pure product sales meters from a blending dispenser.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:FPMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FPMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelProductID\"/>\r\n				<xs:element ref=\"nax:FPMNonResettableTotals\" maxOccurs=\"unbounded\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FPMNonResettableTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelPositionID\"/>\r\n				<xs:element ref=\"nax:FuelProductNonResettableAmountNumber\"/>\r\n				<xs:element ref=\"nax:FuelProductNonResettableVolumeNumber\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--ItemSalesMovement structures-->\r\n	<xs:element name=\"ItemSalesMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Item Sales Movement (ISM) - Contains those data elements the\r\nPOS system needs to send to the BO system to report item sales. It\r\nis implied that all item sales are included in the Merchandise Code\r\nMovement table (MCM) at the summary level.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:ISMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"ISMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ItemCode\"/>\r\n				<xs:element ref=\"nax:ItemID\"/>\r\n				<xs:element ref=\"nax:Description\"/>\r\n				<xs:element ref=\"nax:MerchandiseCode\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:SellingUnits\" minOccurs=\"0\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:ISMTenderSummary\"/>\r\n					<xs:element ref=\"nax:ISMSellPriceSummary\"/>\r\n					<xs:element ref=\"nax:ISMReasonSummary\"/>\r\n					<xs:element ref=\"nax:ISMSalesTotals\"/>\r\n				</xs:choice>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"ISMTenderSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:Tender\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:ISMSellPriceSummary\"/>\r\n					<xs:element ref=\"nax:ISMReasonSummary\"/>\r\n					<xs:element ref=\"nax:ISMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"ISMSellPriceSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ActualSalesPrice\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:ISMReasonSummary\"/>\r\n					<xs:element ref=\"nax:ISMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"ISMReasonSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ISMReason\"/>\r\n				<xs:element ref=\"nax:ISMSalesTotals\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"ISMReason\" type=\"nax:ismReason\"/>\r\n	<xs:element name=\"ISMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:SalesQuantity\"/>\r\n				<xs:element ref=\"nax:SalesAmount\"/>\r\n				<xs:element ref=\"nax:DiscountAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PriceOverrideAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PriceOverrideCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PromotionAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PromotionCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TransactionCount\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--MerchandiseCodeMovement structures-->\r\n	<xs:element name=\"MerchandiseCodeMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Merchandise Code Movement (MCM) - Contains those data\r\nelements the POS system needs to send to the BO system to\r\nreport merchandise code level sales. It is implied that both item\r\nsales and fuel grade sales are included in this table at some\r\nsummary level depending upon implementation setup.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:MCMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"MCMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MerchandiseCode\"/>\r\n				<xs:element ref=\"nax:MerchandiseCodeDescription\"/>\r\n				<xs:element ref=\"nax:MCMSalesTotals\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"MCMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:DiscountAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PromotionAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PromotionCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:SalesQuantity\"/>\r\n				<xs:element ref=\"nax:SalesAmount\"/>\r\n				<xs:element ref=\"nax:TransactionCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:VoidAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:VoidCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:OpenDepartmentSalesAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:OpenDepartmentTransactionCount\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--MiscellaneousSummaryMovement structures-->\r\n	<xs:element name=\"MiscellaneousSummaryMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Miscellaneous Summary Movement (MSM) - Contains those data\r\nelements the POS system needs to send to the BO system to\r\nreport “non-sale” summary level detail for miscellaneous\r\ntransactions, such as vendor payouts.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:MSMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"MSMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MiscellaneousSummaryCodes\"/>\r\n				<xs:element ref=\"nax:MSMSalesTotals\" maxOccurs=\"unbounded\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"MSMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:sequence minOccurs=\"0\">\r\n					<xs:element ref=\"nax:Tender\" maxOccurs=\"unbounded\"/>\r\n					<xs:element ref=\"nax:TenderTransactionsCount\" minOccurs=\"0\"/>\r\n				</xs:sequence>\r\n				<xs:element ref=\"nax:ManualEntryFlag\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:Currency\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:MiscellaneousSummaryAmount\"/>\r\n				<xs:element ref=\"nax:MiscellaneousSummaryCount\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--TankProductMovement structures-->\r\n	<xs:element name=\"TankProductMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Tank Product Movement (TPM) - Contains those data elements the\r\nPOS system needs to send to the BO system to report fuel product\r\ninventory levels.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:TankProdMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TPMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"TPMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:TankID\"/>\r\n				<xs:element ref=\"nax:FuelProductID\"/>\r\n				<xs:choice>\r\n					<xs:element ref=\"nax:FuelProductDepth\"/>\r\n					<xs:element ref=\"nax:FuelProductVolume\"/>\r\n				</xs:choice>\r\n				<xs:element ref=\"nax:FuelProductTemperature\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:Ullage\" minOccurs=\"0\"/>\r\n				<xs:choice>\r\n					<xs:element ref=\"nax:WaterDepth\"/>\r\n					<xs:element ref=\"nax:WaterVolume\"/>\r\n				</xs:choice>\r\n				<xs:element ref=\"nax:ClosingTemperatureCorrectedVolume\" minOccurs=\"0\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--TaxLevelMovement structures-->\r\n	<xs:element name=\"TaxLevelMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Tax Level Movement (TLM) – Contains those data elements the\r\nPOS system needs to send to the BO system to report tax\r\ncollection information by tax level including taxable, tax exempt\r\nand tax forgiven amounts.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TLMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"TLMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:TaxLevelID\"/>\r\n				<xs:element ref=\"nax:MerchandiseCode\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TaxStrategyID\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TaxableSalesAmount\"/>\r\n				<xs:element ref=\"nax:TaxableSalesRefundedAmount\"/>\r\n				<xs:element ref=\"nax:TaxCollectedAmount\"/>\r\n				<xs:element ref=\"nax:TaxExemptSalesAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TaxExemptSalesRefundedAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TaxForgivenSalesAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TaxForgivenSalesRefundedAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TaxRefundedAmount\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--TenderSummaryMovement structures-->\r\n	<xs:element name=\"TenderSummaryMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Tender Summary Movement (TSM) - Contains those data\r\nelements the POS system needs to send to the BO system to\r\nreport “Tender” summary level detail for all financial transactions.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TSMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"TSMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:Tender\"/>\r\n				<xs:element ref=\"nax:TSMSalesTotals\" maxOccurs=\"unbounded\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"TSMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:Currency\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TenderAmount\"/>\r\n				<xs:element ref=\"nax:TenderTransactionsCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:VoidAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:VoidCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:LoanAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:SafeLoanReason\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DropAmount\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--BatchSummaryMovement structures-->\r\n	<xs:element name=\"BatchSummaryMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Batch Summary Movement (BSM) - Contains those data\r\nelements the POS system needs to send to the BO system to\r\nreport “Batch” summary level detail for all Authorized transactions.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:BSMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"BSMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:AuthorizingTerminalID\"/>\r\n				<xs:element name=\"BSMTotalSales\">\r\n					<xs:complexType>\r\n						<xs:sequence>\r\n							<xs:element ref=\"nax:BatchNumber\"/>\r\n							<xs:element ref=\"nax:BatchAmount\" minOccurs=\"0\"/>\r\n							<xs:element ref=\"nax:BatchCount\" minOccurs=\"0\"/>\r\n							<xs:element ref=\"nax:BatchCloseReason\" minOccurs=\"0\"/>\r\n							<xs:element ref=\"nax:BatchStatus\" minOccurs=\"0\"/>\r\n							<xs:element ref=\"nax:BatchType\" minOccurs=\"0\"/>\r\n							<xs:group ref=\"nax:extension\"/>\r\n						</xs:sequence>\r\n					</xs:complexType>\r\n				</xs:element>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"BSMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:Currency\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TenderAmount\"/>\r\n				<xs:element ref=\"nax:TenderTransactionsCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:VoidAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:VoidCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:LoanAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:SafeLoanReason\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DropAmount\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!---->\r\n</xs:schema>\r\n'),(4,44,'','','./Uploads/POS33\\0000044-NAXML-PBIMovement33_modified.xsd','0000044-NAXML-PBIMovement33_modified.xsd','./Uploads/POS33\\',28500,'text/xml','2005-01-21 15:05:24','<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<!-- edited with XML Spy v4.3 U (http://www.xmlspy.com) by Fred Hoff (AutoGas) -->\r\n<!-- edited with XMLSPY v2004 rel. 4 U (http://www.xmlspy.com) by John C Hervey (NACS) -->\r\n<!--Copyright Petroleum Convenience Alliance for Technology Standards 2004. All Rights Reserved.\r\nThis document may be copied, used or shared among PCATS members for purposes consistent with adoption of the PCATS Standards; however, any inconsistent uses must be pre-approved in writing by the Petroleum Convenience Alliance for Technology Standards.  As such, this document may not be furnished to non-members of PCATS, and derivative works that comment on or otherwise explain it or assist in its implementation may not cite or refer to the standard, specification, protocol or guideline, in whole or in part, without such permission.  Moreover, this document may not be modified in any way, including removal of the copyright notice or references to PCATS.  Translations of this document into languages other than English shall continue to reflect the PCATS copyright notice.\r\nThe limited permissions granted above are perpetual and will not be revoked by the Petroleum Convenience Alliance for Technology Standards or its successors or assigns. -->\r\n<!-- \r\n	History\r\n	David Ezell	2002-07		Creation\r\n	David Ezell	2002-11-07	      Fixes based on UML importation attempt, per John Hervey\r\n	David Ezell 2003-10-24      Changes based on diffs between version 3.2 and version3.3 of the DTDs\r\n	David Ezell 2003-12-14      Submitted changes for POSBO approval\r\n	David Ezell 2004-01-09      Changed ComboID instances to PromotionID\r\n	David Ezell 2004-02-17      Removed NACS Copyright and replaced with PCATS Copyright\r\n	David Ezell 2004-03-03      Made Tender element optional in MSMSalesTotals\r\n							   		Restrict TenderTransactionsCount to only occur if Tender occurs in MSMSalesTotals\r\n`									Changed annotation for FuelProductMovement\r\n									CBMSalesTotals/MerchandiseCode and CBMSalesTotals/Tender both optional\r\n									TLMDetail/MerchandiseCode optional\r\n	David Ezell 2004-03-12		Added PriceOverrideAmount and PriceOverrideCount to ISMSalesTotals\r\n	John Hervey 2004-03-15 Changed TankProductMovement to include TankProdMovementHeader v. SalesMovementHeader\r\n	John Hervey 2004-03-15 Changed OpenDepartmentSalesCount to OpenDepartmentTransactionCount to be consistent with dtd and other counts\r\n-->\r\n<xs:schema targetNamespace=\"http://www.naxml.org/POSBO/Vocabulary/2003-10-16\" xmlns:xs=\"http://www.w3.org/2001/XMLSchema\" xmlns:nax=\"http://www.naxml.org/POSBO/Vocabulary/2003-10-16\" elementFormDefault=\"qualified\" attributeFormDefault=\"unqualified\">\r\n	<xs:include schemaLocation=\"NAXML-PBIDictionary33.xsd\"/>\r\n	<xs:include schemaLocation=\"NAXML-PBIUtility33.xsd\"/>\r\n	<!--NAXML-MovementReport structure-->\r\n	<xs:element name=\"NAXML-MovementReport\">\r\n		<xs:annotation>\r\n			<xs:documentation>MovementReport contains a TransmissionHeader and some set of\r\nxxxMovement reports. NAXML-PBIDictionary.dtd contains all of the elements listed in\r\nthe NACS Point-of-Sale Back Office Interface Guidelines Version 3.3</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:TransmissionHeader\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:BatchSummaryMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:ComboSalesMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:FuelGradeMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:FuelProductMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:ItemSalesMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:MerchandiseCodeMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:MiscellaneousSummaryMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:TankProductMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:TaxLevelMovement\" minOccurs=\"0\"/>\r\n					<xs:element ref=\"nax:TenderSummaryMovement\" minOccurs=\"0\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n			<xs:attribute name=\"version\" fixed=\"3.3\"/>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"MovementHeader\">\r\n		<xs:annotation>\r\n			<xs:documentation>Each xxxMovement element contains a MovementHeader and either a\r\nSalesMovementHeader or a TankProdMovementHeader.  MovementHeader contains a set of elements that identify the specific\r\nreport as to date/time and reporting period. Optional \"qualifier\" fields\r\nare also included for a finer grained report.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ReportSequenceNumber\"/>\r\n				<xs:element ref=\"nax:PrimaryReportPeriod\"/>\r\n				<xs:element ref=\"nax:SecondaryReportPeriod\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:BusinessDate\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:BeginDate\"/>\r\n				<xs:element ref=\"nax:BeginTime\"/>\r\n				<xs:element ref=\"nax:EndDate\"/>\r\n				<xs:element ref=\"nax:EndTime\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"SalesMovementHeader\">\r\n		<xs:annotation>\r\n			<xs:documentation>SalesMovementHeader contains a set of elements that identify\r\nthe specific report as to cashier, register and till </xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:RegisterID\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:CashierID\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TillID\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:EmployeeNumber\" minOccurs=\"0\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"TankProdMovementHeader\">\r\n		<xs:annotation>\r\n			<xs:documentation>TankProdMovementHeader contains a set of elements that identify the\r\nspecific report as to date/time of the tank inventory reading</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ReadingDate\"/>\r\n				<xs:element ref=\"nax:ReadingTime\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--ComboSalesMovement structures-->\r\n	<xs:element name=\"ComboSalesMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Combo Sales Movement(CBM) – Contains those data elements the POS system needs to send to the BO system to \"combo\" sales.  A combo is defined as\r\n“selling a specific set of items at a promotional price such as 1\r\ndrink, 1 sandwich, and 1 bag of chips.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:CBMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"CBMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:PromotionID\"/>\r\n				<xs:element ref=\"nax:ComboDescription\"/>\r\n				<xs:element ref=\"nax:ComboPrice\"/>\r\n				<xs:element ref=\"nax:ComboList\" maxOccurs=\"unbounded\"/>\r\n				<xs:element ref=\"nax:CBMSalesTotals\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"CBMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MerchandiseCode\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:Tender\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:ActualSalesPrice\"/>\r\n				<xs:element ref=\"nax:CBMReason\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:SellingUnits\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:Currency\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:InventoryValuePrice\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PromotionAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PromotionCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RefundCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:RegularSellPrice\"/>\r\n				<xs:element ref=\"nax:SalesQuantity\"/>\r\n				<xs:element ref=\"nax:SalesAmount\"/>\r\n				<xs:element ref=\"nax:TransactionCount\" minOccurs=\"0\"/>\r\n				<!-- changed nax:Currency to nax:CurrencyCode -->\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"CBMReason\" type=\"nax:cbmReason\"/>\r\n	<!--FuelGradeMovement structures-->\r\n	<xs:element name=\"FuelGradeMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Fuel Grade Movement (FGM) - Contains those data elements the\r\nPOS system needs to send to the BO system to report fuel grade\r\nlevel sales. For a complete definition of fuel grade refer to the\r\nGlossary. It is implied that all fuel grade sales are included in the\r\nMerchandise Code Movement table (MCM) at the summary level\r\neither in total or by grade depending upon implementation setup.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:FGMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelGradeID\"/>\r\n				<xs:element ref=\"nax:FGMNonResettableTotal\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMTenderSummary\"/>\r\n					<xs:element ref=\"nax:FGMSellPriceSummary\"/>\r\n					<xs:element ref=\"nax:FGMReasonSummary\"/>\r\n					<xs:element ref=\"nax:FGMPositionSummary\"/>\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n					<xs:group ref=\"nax:extension\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMTenderSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:Tender\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMSellPriceSummary\"/>\r\n					<xs:element ref=\"nax:FGMReasonSummary\"/>\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMSellPriceSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ActualSalesPrice\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMReasonSummary\"/>\r\n					<xs:element ref=\"nax:FGMPositionSummary\"/>\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMReasonSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FGMReason\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMPositionSummary\"/>\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMPositionSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelPositionID\"/>\r\n				<xs:element ref=\"nax:FGMNonResettableTotal\" minOccurs=\"0\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMTimeTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMReason\" type=\"nax:fgmReason\"/>\r\n	<xs:element name=\"FGMTimeTierSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:TimeTierCode\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMPriceTierSummary\"/>\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMPriceTierSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:PriceTierCode\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMServiceLevelSummary\"/>\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMServiceLevelSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:ServiceLevelCode\"/>\r\n				<xs:choice maxOccurs=\"unbounded\">\r\n					<xs:element ref=\"nax:FGMOutsideSalesSummary\"/>\r\n					<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n				</xs:choice>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMOutsideSalesSummary\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:OutsideSalesFlag\"/>\r\n				<xs:element ref=\"nax:FGMSalesTotals\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMSalesTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelGradeSalesVolume\"/>\r\n				<xs:element ref=\"nax:FuelGradeSalesAmount\"/>\r\n				<xs:element ref=\"nax:PumpTestTotals\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:TaxExemptSalesVolume\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountCount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DispenserDiscountAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DispenserDiscountCount\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"PumpTestTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:PumpTestAmount\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:PumpTestVolume\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:ReturnTankID\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FGMNonResettableTotal\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelGradeNonResettableTotalVolume\"/>\r\n				<xs:element ref=\"nax:FuelGradeNonResettableTotalAmount\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--FuelProductMovement structures-->\r\n	<xs:element name=\"FuelProductMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Fuel Product Movement (FPM) Allows the transmission of pure product sales meters from a blending dispenser.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:MovementHeader\"/>\r\n				<xs:element ref=\"nax:SalesMovementHeader\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:FPMDetail\" maxOccurs=\"unbounded\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FPMDetail\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelProductID\"/>\r\n				<xs:element ref=\"nax:FPMNonResettableTotals\" maxOccurs=\"unbounded\"/>\r\n				<xs:group ref=\"nax:extension\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"FPMNonResettableTotals\">\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:FuelPositionID\"/>\r\n				<xs:element ref=\"nax:FuelProductNonResettableAmountNumber\"/>\r\n				<xs:element ref=\"nax:FuelProductNonResettableVolumeNumber\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<!--ItemSalesMovement structures-->\r\n	<xs:element name=\"ItemSalesMovement\">\r\n		<xs:annotation>\r\n			<xs:documentation>Item Sales Movement (ISM) - Contains those data elements the\r\nPOS system needs to send to the BO system to report item sales. It\r\nis implied that all item sales are included in the Merchandise Code\r\nMovement table (MCM) at the summary level.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:comple