t name=\"Discount\">\r\n		<xs:annotation>\r\n			<xs:documentation>All information for setting up a discount.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:sequence>\r\n				<xs:element ref=\"nax:DiscountID\"/>\r\n				<xs:element ref=\"nax:Description\"/>\r\n				<xs:element ref=\"nax:ReceiptDescription\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountType\"/>\r\n				<xs:element ref=\"nax:DiscountValue\"/>\r\n				<xs:element ref=\"nax:IsHALO\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:IsEnabled\"/>\r\n				<xs:element ref=\"nax:TaxStrategyID\" minOccurs=\"0\" maxOccurs=\"unbounded\"/>\r\n				<xs:element ref=\"nax:MaxDiscountValue\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:MaxDiscountVolume\" minOccurs=\"0\"/>\r\n				<xs:element ref=\"nax:DiscountRestriction\" minOccurs=\"0\"/>\r\n			</xs:sequence>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"DiscountID\" type=\"nax:id16\">\r\n		<xs:annotation>\r\n			<xs:documentation>Unique code for the discount</xs:documentation>\r\n		</xs:annotation>\r\n	</xs:element>\r\n	<xs:element name=\"Description\" type=\"nax:description40\">\r\n		<xs:annotation>\r\n			<xs:documentation>Name of the discount</xs:documentation>\r\n		</xs:annotation>\r\n	</xs:element>\r\n	<xs:element name=\"ReceiptDescription\" type=\"nax:description40\">\r\n		<xs:annotation>\r\n			<xs:documentation>Description for printing on the receipt. Defaults to the discount description if not supplied</xs:documentation>\r\n		</xs:annotation>\r\n	</xs:element>\r\n	<xs:element name=\"DiscountType\">\r\n		<xs:annotation>\r\n			<xs:documentation>Type of discount - enumerated type.</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:simpleType>\r\n			<xs:restriction base=\"nax:description24\">\r\n				<xs:enumeration value=\"OpenItemPercentage\">\r\n					<xs:annotation>\r\n						<xs:documentation>Open Item Percentage Discount - Same as FixedItemPercentage, but the discount rate is at the cashiers discretion.  allows the cashier to select the discount percentage at the sale time.  Can be capped by a HALO  e.g. up to 10% off chocolate milk</xs:documentation>\r\n					</xs:annotation>\r\n				</xs:enumeration>\r\n				<xs:enumeration value=\"FixedItemPercentage\">\r\n					<xs:annotation>\r\n						<xs:documentation>Fixed Item Percentage Discount - fixed percentage value, cannot be overridden. E.g. 7% off Diesel</xs:documentation>\r\n					</xs:annotation>\r\n				</xs:enumeration>\r\n				<xs:enumeration value=\"OpenItemQty\">\r\n					<xs:annotation>\r\n						<xs:documentation>Open Item Qty Discount - Same as FixedItemQty, but the discount rate is at the cashiers discretion. Can be capped by a HALO</xs:documentation>\r\n					</xs:annotation>\r\n				</xs:enumeration>\r\n				<xs:enumeration value=\"FixedItemQty\">\r\n				<xs:annotation>\r\n						<xs:documentation>Fixed Item Qty Discount - A value off per unit sold - Allows the cashier to select the value at time of sale.  E.g. 4cents per litre discount on fuel.  $1 off every 2Litre milk purchased.  Predefined discount per unit amount, cannot be overridden.</xs:documentation>\r\n					</xs:annotation>\r\n				</xs:enumeration>\r\n				<xs:enumeration value=\"OpenSalePercentage\">\r\n					<xs:annotation>\r\n						<xs:documentation>Open Sale percentage discount - Same as FixedSalePercentage, but the discount rate is at the cashiers discretion. Can be capped by a HALO</xs:documentation>\r\n					</xs:annotation>\r\n				</xs:enumeration>				\r\n				<xs:enumeration value=\"FixedSalePercentage\">\r\n					<xs:annotation>\r\n						<xs:documentation>Fixed Sale percentage discount - fixed percentage value off the entire sale, cannot be overridden. E.g. 5% off sale total</xs:documentation>\r\n					</xs:annotation>\r\n				</xs:enumeration>				\r\n				<xs:enumeration value=\"OpenSaleAmount\">\r\n					<xs:annotation>\r\n						<xs:documentation>Open Sale Amount discount - Same as FixedSaleAmount, but the discount rate is at the cashiers discretion. Can be capped by a HALO</xs:documentation>\r\n					</xs:annotation>\r\n				</xs:enumeration>				\r\n				<xs:enumeration value=\"FixedSaleAmount\">\r\n					<xs:annotation>\r\n						<xs:documentation>Fixed Sale Amount discount - fixed amount (dollar value) off the entire sale, cannot be overridden. E.g. $2 off sale total</xs:documentation>\r\n					</xs:annotation>\r\n				</xs:enumeration>				\r\n			</xs:restriction>\r\n		</xs:simpleType>\r\n	</xs:element>\r\n	<xs:element name=\"DiscountValue\">\r\n		<xs:complexType>\r\n			<xs:choice>\r\n				<xs:element ref=\"nax:DiscountAmount\"/>\r\n				<xs:element ref=\"nax:DiscountAmountPerUnit\"/>\r\n				<xs:element ref=\"nax:DiscountPercent\"/>\r\n				<xs:element ref=\"nax:DiscountPercentPerUnit\"/>\r\n				<xs:element ref=\"nax:DiscountQty\"/>\r\n			</xs:choice>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"DiscountAmount\" type=\"nax:amount12\">\r\n		<xs:annotation>\r\n			<xs:documentation>A discount rate: dollar value</xs:documentation>\r\n		</xs:annotation>\r\n	</xs:element>\r\n	<xs:element name=\"DiscountAmountPerUnit\">\r\n		<xs:annotation>\r\n			<xs:documentation>A discount rate: dollar value per unit</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:simpleContent>\r\n				<xs:extension base=\"nax:amount12\">\r\n					<xs:attribute name=\"uom\" type=\"nax:salesUOM\" use=\"optional\"/>\r\n				</xs:extension>\r\n			</xs:simpleContent>\r\n		</xs:complexType>\r\n	</xs:element>\r\n	<xs:element name=\"DiscountPercent\" type=\"nax:percentage\">\r\n		<xs:annotation>\r\n			<xs:documentation>A discount rate: percentage</xs:documentation>\r\n		</xs:annotation>\r\n	</xs:element>\r\n	<xs:element name=\"DiscountPercentPerUnit\">\r\n		<xs:annotation>\r\n			<xs:documentation>A discount rate: percentage per unit</xs:documentation>\r\n		</xs:annotation>\r\n		<xs:complexType>\r\n			<xs:simpleContent>\r\n				<xs:extension base=\"nax:percentage\">\r\n					<xs:attribute name=\"uom\" type=\"nax:salesUOM\" use=\"optional